import React, { useState, useEffect } from 'react';
import { BarChart3, RefreshCw, Trash2 } from 'lucide-react';

interface HeaderProps {
  onClearChat: () => void;
}

export function Header({ onClearChat }: HeaderProps) {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isMarketOpen, setIsMarketOpen] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      setCurrentTime(now);

      // Market hours: 9:30 AM - 4:00 PM ET, Monday-Friday
      const easternTime = new Date(now.toLocaleString("en-US", {timeZone: "America/New_York"}));
      const day = easternTime.getDay(); // 0 = Sunday, 1 = Monday, etc.
      const hours = easternTime.getHours();
      const minutes = easternTime.getMinutes();
      const totalMinutes = hours * 60 + minutes;

      // Market is open Monday (1) to Friday (5), 9:30 AM to 4:00 PM ET
      const isWeekday = day >= 1 && day <= 5;
      const marketOpenTime = 9 * 60 + 30; // 9:30 AM in minutes
      const marketCloseTime = 16 * 60; // 4:00 PM in minutes
      const isWithinMarketHours = totalMinutes >= marketOpenTime && totalMinutes < marketCloseTime;

      setIsMarketOpen(isWeekday && isWithinMarketHours);
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  return (
    <header className="sticky top-0 z-10 bg-gray-950/90 backdrop-blur-xl border-b border-gray-700/50 px-4 py-3">
      <div className="max-w-4xl mx-auto flex items-center justify-between">
        <div className="flex items-center gap-5">
          <div className="w-14 h-14 bg-gradient-to-br from-blue-500 to-blue-600 rounded-2xl flex items-center justify-center shadow-xl">
            <BarChart3 className="w-8 h-8 text-white" />
          </div>
          <div>
            <h1 className="text-3xl font-bold text-white tracking-tight leading-tight" style={{fontFamily: 'Space Grotesk, Inter, system-ui, sans-serif'}}>
              AI Stock Assistant
            </h1>
            <p className="text-base text-gray-300 font-medium tracking-wide">Market Intelligence Platform</p>
          </div>
        </div>

        <div className="flex items-center gap-6">
          <div className="text-right">
            <div className="text-white font-mono text-2xl font-bold tracking-wide mb-1">{formatTime(currentTime)}</div>
            <div className={`text-sm px-3 py-1.5 rounded-lg font-semibold ${
              isMarketOpen 
                ? 'bg-green-900/40 text-green-300 border border-green-700/50' 
                : 'bg-red-900/40 text-red-300 border border-red-700/50'
            }`}>
              Market {isMarketOpen ? 'Open' : 'Closed'}
            </div>
          </div>

          <div className="flex items-center gap-3 px-4 py-2 bg-green-900/40 text-green-300 rounded-lg text-base font-semibold border border-green-700/50">
            <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse shadow-lg shadow-green-400/50"></div>
            Live
          </div>
        </div>
      </div>
    </header>
  );
}
