import React, { useEffect, useRef, useState } from 'react';
import { MessageCircle } from 'lucide-react';
import { Header } from './components/Header';
import { Message } from './components/Message';
import { TypingIndicator } from './components/TypingIndicator';
import { MessageInput } from './components/MessageInput';
import { QuickActions } from './components/QuickActions';
import { RedditDashboard } from './components/RedditDashboard';
import { useChat } from './hooks/useChat';

function App() {
  const { messages, isLoading, sendMessage, clearMessages } = useChat();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [isRedditDashboardOpen, setIsRedditDashboardOpen] = useState(false);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  const showQuickActions = messages.length <= 1 && !isLoading;

  return (
    <div className="flex flex-col h-screen" style={{background: 'linear-gradient(to bottom right, var(--whisper-white), var(--light-fog))', color: 'var(--graphite-gray)'}}>
      {/* Subtle grain texture overlay */}
      <div className="absolute inset-0 opacity-[0.015] bg-[url('data:image/svg+xml;base64,')] pointer-events-none"></div>

      {/* Main Content Container */}
      <div className={`flex flex-col h-full transition-all duration-300 ${isRedditDashboardOpen ? 'mr-96' : ''}`}>
        <Header onClearChat={clearMessages} />

        <main className="flex-1 overflow-y-auto relative">
          <div className="max-w-4xl mx-auto px-6 py-8">
            {showQuickActions && (
              <QuickActions onActionClick={sendMessage} isLoading={isLoading} />
            )}

            <div className="space-y-0">
              {messages.map((message) => (
                <Message 
                  key={message.id} 
                  message={message} 
                  onFollowUpClick={sendMessage}
                  isLoading={isLoading}
                />
              ))}

              {isLoading && <TypingIndicator />}

              <div ref={messagesEndRef} />
            </div>
          </div>
        </main>

        <MessageInput onSendMessage={sendMessage} isLoading={isLoading} />
      </div>

      {/* Floating Reddit Pulse Button */}
      {!isRedditDashboardOpen && (
        <button
          onClick={() => setIsRedditDashboardOpen(true)}
          className="fixed bottom-6 right-6 bg-gradient-to-r from-blue-600 to-purple-600 text-white p-3 rounded-full shadow-lg hover:shadow-xl transition-all duration-200 z-30 flex items-center gap-2"
          title="Open Reddit Market Pulse"
        >
          <MessageCircle className="w-5 h-5" />
          <span className="text-sm font-medium">Reddit Pulse</span>
        </button>
      )}

      <RedditDashboard 
        isOpen={isRedditDashboardOpen}
        onClose={() => setIsRedditDashboardOpen(false)}
      />
    </div>
  );
}

export default App;
