import React from 'react';
import { DollarSign, BarChart3, Clock, Building2, Newspaper, Users } from 'lucide-react';

interface QuickActionsProps {
  onActionClick: (message: string) => void;
  isLoading: boolean;
}

const quickActions = [
  {
    icon: DollarSign,
    iconColor: "text-blue-400",
    label: "Stock Price",
    message: "What's AAPL stock price?",
    description: "Get real-time stock quotes"
  },
  {
    icon: BarChart3,
    iconColor: "text-yellow-400", 
    label: "Compare Stocks",
    message: "Compare AAPL vs GOOGL vs MSFT",
    description: "Side-by-side stock analysis"
  },
  {
    icon: Clock,
    iconColor: "text-green-400",
    label: "Market Hours",
    message: "What are market hours today?",
    description: "Trading schedules & holidays"
  },
  {
    icon: Building2,
    iconColor: "text-blue-400",
    label: "Company Info",
    message: "Tell me about Apple company", 
    description: "Business details & fundamentals"
  },
  {
    icon: Newspaper,
    iconColor: "text-red-400",
    label: "Latest News",
    message: "Recent news for Tesla",
    description: "Breaking market news"
  },
  {
    icon: Users,
    iconColor: "text-green-400",
    label: "Insider Trading", 
    message: "Show me insider trading for NVDA",
    description: "Executive buy/sell activity"
  }
];

export function QuickActions({ onActionClick, isLoading }: QuickActionsProps) {
  return (
    <div className="mb-8">
      <div className="text-center mb-6">
        <h2 className="text-2xl font-semibold text-white mb-2">What can I help you with?</h2>
        <p className="text-gray-400">Choose a category below or ask me anything about the markets</p>
      </div>

      <div className="grid grid-cols-3 gap-4">
        {quickActions.map((action, index) => {
          const Icon = action.icon;
          return (
            <button
              key={index}
              onClick={() => onActionClick(action.message)}
              disabled={isLoading}
              className="bg-gray-900/50 border border-gray-800 rounded-lg p-4 text-left hover:bg-gray-800/50 hover:border-gray-700 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed group"
            >
              <div className="flex items-center gap-3 mb-2">
                <Icon className={`w-5 h-5 ${action.iconColor}`} />
                <span className="font-medium text-white">{action.label}</span>
              </div>
              <p className="text-sm text-gray-400 group-hover:text-gray-300">
                {action.description}
              </p>
            </button>
          );
        })}
      </div>
    </div>
  );
}
