import React from 'react';
import { TrendingUp, BarChart3, Clock, Building2, Newspaper, Users, Activity, PieChart, Search } from 'lucide-react';

interface DynamicSuggestionsProps {
  userInput: string;
  onSuggestionClick: (message: string) => void;
  isLoading: boolean;
}

const getIconAndColor = (suggestion: string) => {
  const lower = suggestion.toLowerCase();

  if (lower.includes('price') || lower.includes('quote')) {
    return {
      icon: TrendingUp,
      color: "text-green-600 bg-green-50 border-green-200"
    };
  }

  if (lower.includes('compare') || lower.includes('vs')) {
    return {
      icon: BarChart3,
      color: "text-blue-600 bg-blue-50 border-blue-200"
    };
  }

  if (lower.includes('history') || lower.includes('performance')) {
    return {
      icon: Activity,
      color: "text-red-600 bg-red-50 border-red-200"
    };
  }

  if (lower.includes('gainers') || lower.includes('movers')) {
    return {
      icon: PieChart,
      color: "text-emerald-600 bg-emerald-50 border-emerald-200"
    };
  }

  if (lower.includes('hours') || lower.includes('market')) {
    return {
      icon: Clock,
      color: "text-purple-600 bg-purple-50 border-purple-200"
    };
  }

  if (lower.includes('company') || lower.includes('overview')) {
    return {
      icon: Building2,
      color: "text-orange-600 bg-orange-50 border-orange-200"
    };
  }

  if (lower.includes('news') || lower.includes('articles')) {
    return {
      icon: Newspaper,
      color: "text-cyan-600 bg-cyan-50 border-cyan-200"
    };
  }

  if (lower.includes('insider') || lower.includes('trading')) {
    return {
      icon: Users,
      color: "text-pink-600 bg-pink-50 border-pink-200"
    };
  }

  return {
    icon: Search,
    color: "text-gray-600 bg-gray-50 border-gray-200"
  };
};

const generateSuggestions = (userInput: string): string[] => {
  const input = userInput.toLowerCase().trim();

  if (!input) return [];

  // Extract potential symbols
  const words = input.toUpperCase().split(/\s+/);
  const potentialSymbols = words.filter(word => 
    word.length >= 1 && word.length <= 5 && 
    /^[A-Z]+$/.test(word) &&
    !['THE', 'AND', 'OR', 'FOR', 'WITH', 'VS', 'TO', 'IN', 'ON', 'AT', 'IS', 'BE', 'DO', 'GO', 'SO', 'NO', 'MY', 'WE', 'ME', 'UP', 'IT'].includes(word)
  );

  const primarySymbol = potentialSymbols[0] || 'AAPL';
  const suggestions: string[] = [];

  // Stock price suggestions
  if (input.includes('price') || input.includes('quote') || potentialSymbols.length > 0) {
    if (!input.includes('history') && !input.includes('historical')) {
      suggestions.push(`What's ${primarySymbol} current stock price?`);
    }
  }

  // Comparison suggestions
  if (input.includes('compare') || input.includes('vs') || input.includes('versus') || potentialSymbols.length > 1) {
    suggestions.push(`Compare ${primarySymbol} vs GOOGL vs MSFT`);
  } else if (potentialSymbols.length === 1) {
    suggestions.push(`Compare ${primarySymbol} with other stocks`);
  }

  // Historical data suggestions
  if (input.includes('history') || input.includes('performance') || input.includes('chart') || input.includes('trend')) {
    suggestions.push(`Show me 6 month price history for ${primarySymbol}`);
  } else if (potentialSymbols.length > 0) {
    suggestions.push(`Historical performance of ${primarySymbol}`);
  }

  // Company info suggestions
  if (input.includes('company') || input.includes('about') || input.includes('overview') || input.includes('profile')) {
    suggestions.push(`Tell me about ${primarySymbol} company`);
  } else if (potentialSymbols.length > 0) {
    suggestions.push(`Company overview of ${primarySymbol}`);
  }

  // News suggestions
  if (input.includes('news') || input.includes('articles') || input.includes('headlines')) {
    suggestions.push(`Recent news for ${primarySymbol}`);
  } else if (potentialSymbols.length > 0) {
    suggestions.push(`Latest news about ${primarySymbol}`);
  }

  // Analyst ratings
  if (input.includes('analyst') || input.includes('rating') || input.includes('recommendation')) {
    suggestions.push(`Analyst ratings for ${primarySymbol}`);
  } else if (potentialSymbols.length > 0) {
    suggestions.push(`What do analysts say about ${primarySymbol}?`);
  }

  // Insider trading
  if (input.includes('insider') || input.includes('trading activity')) {
    suggestions.push(`Insider trading activity for ${primarySymbol}`);
  } else if (potentialSymbols.length > 0) {
    suggestions.push(`Insider activity for ${primarySymbol}`);
  }

  // Market-wide suggestions
  if (input.includes('market') || input.includes('gainers') || input.includes('losers') || input.includes('movers')) {
    suggestions.push('Top market gainers today');
    suggestions.push('Top market losers today');
  }

  // Trading hours
  if (input.includes('hours') || input.includes('open') || input.includes('close') || input.includes('schedule')) {
    suggestions.push('What are market hours today?');
  }

  // If no specific suggestions, add general ones based on detected symbols
  if (suggestions.length === 0 && potentialSymbols.length > 0) {
    suggestions.push(`What's ${primarySymbol} stock price?`);
    suggestions.push(`Tell me about ${primarySymbol} company`);
    suggestions.push(`Recent news for ${primarySymbol}`);
    suggestions.push(`Compare ${primarySymbol} with other stocks`);
  }

  // If no symbols detected, suggest popular queries
  if (suggestions.length === 0) {
    suggestions.push('What\'s AAPL stock price?');
    suggestions.push('Compare AAPL vs GOOGL vs MSFT');
    suggestions.push('What are market hours today?');
    suggestions.push('Top market gainers today');
  }

  // Remove duplicates and limit to 4
  const uniqueSuggestions = Array.from(new Set(suggestions));
  return uniqueSuggestions.slice(0, 4);
};

export function DynamicSuggestions({ userInput, onSuggestionClick, isLoading }: DynamicSuggestionsProps) {
  const suggestions = generateSuggestions(userInput);

  if (suggestions.length === 0 || userInput.length < 2) return null;

  return (
    <div className="absolute bottom-full left-0 right-0 mb-2 bg-white border border-gray-200 rounded-lg shadow-lg z-10 max-h-60 overflow-y-auto">
      <div className="p-3">
        <h4 className="text-xs font-medium text-gray-500 mb-2">ðŸ’¡ Suggested questions</h4>
        <div className="space-y-1">
          {suggestions.map((suggestion, index) => {
            const { icon: Icon, color } = getIconAndColor(suggestion);

            return (
              <button
                key={index}
                onClick={() => {
                  onSuggestionClick(suggestion);
                }}
                disabled={isLoading}
                className={`
                  w-full text-left p-2 rounded-md border transition-all duration-200 text-sm
                  hover:shadow-sm hover:scale-[1.01] disabled:opacity-50 disabled:cursor-not-allowed
                  ${color}
                `}
              >
                <div className="flex items-center gap-2">
                  <Icon className="w-3 h-3 flex-shrink-0" />
                  <span className="truncate">{suggestion}</span>
                </div>
              </button>
            );
          })}
        </div>
      </div>
    </div>
  );
}
