import React, { useState, useRef, KeyboardEvent } from 'react';
import { Send, Loader2 } from 'lucide-react';

interface MessageInputProps {
  onSendMessage: (message: string) => void;
  isLoading: boolean;
}

export function MessageInput({ onSendMessage, isLoading }: MessageInputProps) {
  const [message, setMessage] = useState('');
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const handleSubmit = () => {
    if (message.trim() && !isLoading) {
      onSendMessage(message);
      setMessage('');
      if (textareaRef.current) {
        textareaRef.current.style.height = 'auto';
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  const handleTextareaChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setMessage(e.target.value);

    // Auto-resize textarea
    const textarea = e.target;
    textarea.style.height = 'auto';
    textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
  };

  return (
    <div className="sticky bottom-0 bg-slate-800/90 backdrop-blur-xl border-t border-slate-700/40 p-6">
      <div className="max-w-4xl mx-auto">
        <div className="relative flex items-end gap-4 bg-slate-800/60 backdrop-blur-sm rounded-2xl border border-slate-700/40 p-4 focus-within:ring-2 focus-within:ring-blue-500/50 focus-within:border-blue-500/60 shadow-2xl">
          <textarea
            ref={textareaRef}
            value={message}
            onChange={handleTextareaChange}
            onKeyDown={handleKeyDown}
            placeholder="Real-time quotes • Historical data • Company insights • Market analysis..."
            className="flex-1 resize-none bg-transparent border-none outline-none text-white placeholder-gray-500 px-3 py-3 max-h-[120px] min-h-[40px] text-base leading-relaxed"
            rows={1}
            disabled={isLoading}
          />

          <button
            onClick={handleSubmit}
            disabled={!message.trim() || isLoading}
            className="flex-shrink-0 p-3 bg-gradient-to-br from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 disabled:from-gray-600 disabled:to-gray-700 disabled:cursor-not-allowed text-white rounded-xl transition-all duration-200 shadow-lg hover:shadow-xl hover:scale-105"
          >
            {isLoading ? (
              <Loader2 className="w-5 h-5 animate-spin" />
            ) : (
              <Send className="w-5 h-5" />
            )}
          </button>
        </div>

        <div className="text-center mt-3">
          <p className="text-xs text-gray-400 font-medium">
            Press <kbd className="px-2 py-1 bg-slate-700 text-gray-200 rounded text-xs border border-slate-600">Enter</kbd> to send • <kbd className="px-2 py-1 bg-slate-700 text-gray-200 rounded text-xs border border-slate-600">Shift + Enter</kbd> for new line
          </p>
        </div>
      </div>
    </div>
  );
}
