import { useState, useCallback } from 'react';
import { Message, ChatState } from '../types';
import { sendMessage, ApiError } from '../utils/api';

// Generate smart follow-up suggestions based on user query and response content
const generateFallbackSuggestions = (userQuery: string, assistantResponse: string): string => {
  const query = userQuery.toLowerCase();
  const response = assistantResponse.toLowerCase();

  // Extract potential symbols from the response or query
  const symbolPattern = /\b[A-Z]{1,5}\b/g;
  const symbols = (userQuery + ' ' + assistantResponse).match(symbolPattern) || [];
  const uniqueSymbols = [...new Set(symbols)].filter(s => s.length <= 5).slice(0, 3);
  const primarySymbol = uniqueSymbols[0] || 'AAPL';

  const suggestions: string[] = [];

  // Stock-related suggestions
  if (!response.includes('stock information') && !response.includes('stock price')) {
    suggestions.push(`What's ${primarySymbol} current stock price?`);
  }

  if (!response.includes('comparison') && !response.includes('compare') && uniqueSymbols.length >= 1) {
    suggestions.push(`Compare ${primarySymbol} vs GOOGL vs MSFT`);
  }

  if (!response.includes('history') && !response.includes('performance') && uniqueSymbols.length >= 1) {
    suggestions.push(`Show me 6 month price history for ${primarySymbol}`);
  }

  // Company insights suggestions
  if (!response.includes('company insights') && !response.includes('company overview') && uniqueSymbols.length >= 1) {
    suggestions.push(`Tell me about ${primarySymbol} company`);
  }

  if (!response.includes('news') && !response.includes('articles') && uniqueSymbols.length >= 1) {
    suggestions.push(`Recent news for ${primarySymbol}`);
  }

  if (!response.includes('analyst') && !response.includes('rating') && uniqueSymbols.length >= 1) {
    suggestions.push(`Analyst ratings for ${primarySymbol}`);
  }

  if (!response.includes('insider') && uniqueSymbols.length >= 1) {
    suggestions.push(`Insider trading activity for ${primarySymbol}`);
  }

  // Market-wide suggestions
  if (!response.includes('market movers') && !response.includes('gainers')) {
    suggestions.push('Top market gainers today');
  }

  if (!response.includes('market hours') && !response.includes('trading hours')) {
    suggestions.push('What are market hours today?');
  }

  // If no specific suggestions, add general ones
  if (suggestions.length === 0) {
    suggestions.push('What\'s AAPL stock price?');
    suggestions.push('Compare AAPL vs GOOGL vs MSFT');
    suggestions.push('What are market hours today?');
    suggestions.push('Top market gainers today');
  }

  // Format as numbered list with emojis
  const formattedSuggestions = suggestions.slice(0, 4).map((suggestion, index) => {
    let emoji = 'üîç';
    if (suggestion.includes('price')) emoji = 'üìà';
    else if (suggestion.includes('compare')) emoji = 'üìä';
    else if (suggestion.includes('history')) emoji = 'üìâ';
    else if (suggestion.includes('company') || suggestion.includes('about')) emoji = 'üè¢';
    else if (suggestion.includes('news')) emoji = 'üì∞';
    else if (suggestion.includes('analyst')) emoji = 'üìä';
    else if (suggestion.includes('insider')) emoji = 'üë•';
    else if (suggestion.includes('gainers') || suggestion.includes('movers')) emoji = 'üöÄ';
    else if (suggestion.includes('hours')) emoji = 'üïê';

    return `${index + 1}. ${emoji} ${suggestion}`;
  });

  return `\n\n__FOLLOW_UPS_START__\n${formattedSuggestions.join('\n')}\n__FOLLOW_UPS_END__`;
};

export function useChat(): ChatState & {
  sendMessage: (content: string) => Promise<void>;
  clearMessages: () => void;
} {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: "Hello! I'm your AI Stock Assistant. I can help you with:\n\nüìà **Stock Prices & Quotes** - \"What's AAPL stock price?\"\nüìä **Stock Comparisons** - \"Compare AAPL vs TSLA\"\nüìâ **Historical Performance** - \"NVDA performance last 6 months\"\nüöÄ **Market Movers** - \"Show me top gainers today\"\nüïê **Trading Hours** - \"What are market hours?\"\nüè¢ **Company Information** - \"Tell me about Apple company\"\nüì∞ **Company News** - \"Recent news for Tesla\"\nüë• **Insider Trading** - \"Insider activity for NVDA\"\nüìä **Analyst Ratings** - \"Analyst ratings for AMD\"\n\nWhat would you like to know about the markets?",
      timestamp: new Date(),
    }
  ]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string>();

  const handleSendMessage = useCallback(async (content: string) => {
    if (!content.trim() || isLoading) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: content.trim(),
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);
    setError(undefined);

    try {
      const response = await sendMessage(content.trim());

      // Check if response already contains follow-up suggestions
      let finalResponse = response;
      if (!response.includes('__FOLLOW_UPS_START__')) {
        // Generate fallback suggestions if none exist
        const fallbackSuggestions = generateFallbackSuggestions(content.trim(), response);
        finalResponse = response + fallbackSuggestions;
      }

      const assistantMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: finalResponse,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (err) {
      const errorMessage = err instanceof ApiError ? err.message : 'An unexpected error occurred';
      setError(errorMessage);

      const errorResponse: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: `‚ùå **Error**: ${errorMessage}\n\nPlease try again or check if the AI Stock Assistant server is running.`,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsLoading(false);
    }
  }, [isLoading]);

  const clearMessages = useCallback(() => {
    setMessages([
      {
        id: '1',
        role: 'assistant',
        content: "Hello! I'm your AI Stock Assistant. I can help you with:\n\nüìà **Stock Prices & Quotes** - \"What's AAPL stock price?\"\nüìä **Stock Comparisons** - \"Compare AAPL vs TSLA\"\nüìâ **Historical Performance** - \"NVDA performance last 6 months\"\nüöÄ **Market Movers** - \"Show me top gainers today\"\nüïê **Trading Hours** - \"What are market hours?\"\nüè¢ **Company Information** - \"Tell me about Apple company\"\nüì∞ **Company News** - \"Recent news for Tesla\"\nüë• **Insider Trading** - \"Insider activity for NVDA\"\nüìä **Analyst Ratings** - \"Analyst ratings for AMD\"\n\nWhat would you like to know about the markets?",
        timestamp: new Date(),
      }
    ]);
    setError(undefined);
  }, []);

  return {
    messages,
    isLoading,
    error,
    sendMessage: handleSendMessage,
    clearMessages,
  };
}
