import React, { useEffect, useRef } from 'react';
import { Header } from './components/Header';
import { Message } from './components/Message';
import { TypingIndicator } from './components/TypingIndicator';
import { MessageInput } from './components/MessageInput';
import { QuickActions } from './components/QuickActions';
import { useChat } from './hooks/useChat';

function App() {
  const { messages, isLoading, sendMessage, clearMessages } = useChat();
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  const showQuickActions = messages.length <= 1 && !isLoading;

  return (
    <div className="flex flex-col h-screen bg-black text-white">
      <Header onClearChat={clearMessages} />

      <main className="flex-1 overflow-y-auto relative">
        <div className="max-w-4xl mx-auto px-4 py-6">
          {showQuickActions && (
            <>
              <QuickActions onActionClick={sendMessage} isLoading={isLoading} />

              {/* AI Assistant Section */}
              <div className="mb-8">
                <div className="bg-gray-900/50 border border-gray-800 rounded-lg p-6">
                  <div className="flex items-center gap-3 mb-3">
                    <div className="w-8 h-8 bg-blue-500 rounded-lg flex items-center justify-center">
                      <span className="text-white text-sm font-bold">AI</span>
                    </div>
                    <div>
                      <h3 className="font-semibold text-white">AI Assistant</h3>
                      <p className="text-sm text-gray-400">Ask me anything about the markets</p>
                    </div>
                  </div>
                </div>
              </div>

              {/* Start conversation text */}
              <div className="text-center text-gray-500 mb-8">
                Start a conversation by typing your question below
              </div>
            </>
          )}

          <div className="space-y-0">
            {messages.map((message) => (
              <Message 
                key={message.id} 
                message={message} 
                onFollowUpClick={sendMessage}
                isLoading={isLoading}
              />
            ))}

            {isLoading && <TypingIndicator />}

            <div ref={messagesEndRef} />
          </div>
        </div>

        <MarketClock />
      </main>

      <MessageInput onSendMessage={sendMessage} isLoading={isLoading} />
    </div>
  );
}

export default App;
