import React from 'react';
import { DollarSign, BarChart3, Clock, Building2, Newspaper, Users } from 'lucide-react';

interface QuickActionsProps {
  onActionClick: (message: string) => void;
  isLoading: boolean;
}

const quickActions = [
  {
    icon: DollarSign,
    iconColor: "text-blue-400",
    label: "Stock Price",
    message: "What's AAPL stock price?",
    description: "Get real-time stock quotes"
  },
  {
    icon: BarChart3,
    iconColor: "text-yellow-400", 
    label: "Compare Stocks",
    message: "Compare AAPL vs GOOGL vs MSFT",
    description: "Side-by-side stock analysis"
  },
  {
    icon: Clock,
    iconColor: "text-green-400",
    label: "Market Hours",
    message: "What are market hours today?",
    description: "Trading schedules & holidays"
  },
  {
    icon: Building2,
    iconColor: "text-blue-400",
    label: "Company Info",
    message: "Tell me about Apple company", 
    description: "Business details & fundamentals"
  },
  {
    icon: Newspaper,
    iconColor: "text-red-400",
    label: "Latest News",
    message: "Recent news for Tesla",
    description: "Breaking market news"
  },
  {
    icon: Users,
    iconColor: "text-green-400",
    label: "Insider Trading", 
    message: "Show me insider trading for NVDA",
    description: "Executive buy/sell activity"
  }
];

export function QuickActions({ onActionClick, isLoading }: QuickActionsProps) {
  return (
    <div className="mb-12">
      <div className="text-center mb-8">
        <h2 className="text-3xl font-bold text-white mb-3 tracking-tight" style={{fontFamily: 'Space Grotesk, Inter, system-ui, sans-serif'}}>
          Market Intelligence at Your Fingertips
        </h2>
        <p className="text-gray-400 text-lg font-light">Explore stocks, analyze trends, and make informed decisions</p>
      </div>

      <div className="grid grid-cols-3 gap-5">
        {quickActions.map((action, index) => {
          const Icon = action.icon;
          return (
            <button
              key={index}
              onClick={() => onActionClick(action.message)}
              disabled={isLoading}
              className="bg-gray-900/40 backdrop-blur-sm border border-gray-800/60 rounded-xl p-5 text-left hover:bg-gray-800/50 hover:border-gray-700/80 hover:scale-[1.02] transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed group shadow-lg hover:shadow-xl"
            >
              <div className="flex items-center gap-3 mb-3">
                <div className={`w-8 h-8 rounded-lg bg-gradient-to-br from-gray-800 to-gray-900 flex items-center justify-center`}>
                  <Icon className={`w-4 h-4 ${action.iconColor}`} />
                </div>
                <span className="font-semibold text-white text-base">{action.label}</span>
              </div>
              <p className="text-sm text-gray-400 group-hover:text-gray-300 leading-relaxed">
                {action.description}
              </p>
            </button>
          );
        })}
      </div>
    </div>
  );
}
